import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { login } from '../redux/userSlice';
import { MDBContainer } from 'mdb-react-ui-kit';
import LoginForm from '../components/auth/LoginForm';
import { routes } from '../components/routes/links';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rememberPassword, setRememberPassword] = useState(false);
  const [error, setError] = useState(null);

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const location = useLocation();
  const from = location.state?.from?.pathname || routes.adverts;

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const credentials = { email, password, rememberMe: rememberPassword };
      const action = await dispatch(login(credentials));
      if (login.fulfilled.match(action)) {
        navigate(from, { replace: true });
      } else {
        throw new Error(action.payload || 'Error al iniciar sesi贸n');
      }
    } catch (error) {
      setError('Error al iniciar sesi贸n: ' + error.message);
    }
  };

  return (
    <MDBContainer className="p-3 my-5 d-flex flex-column w-50">
      <LoginForm
        email={email}
        password={password}
        rememberPassword={rememberPassword}
        onEmailChange={(e) => setEmail(e.target.value)}
        onPasswordChange={(e) => setPassword(e.target.value)}
        onRememberPasswordChange={(e) => setRememberPassword(e.target.checked)}
        onSubmit={handleSubmit}
        error={error}
      />
    </MDBContainer>
  );
};

export default LoginPage;
